import React from "react";
import "./Navbar.css";

//handling the navigation
import { HashLink as Link } from "react-router-hash-link";

//handling the translations
import i18next from "../i18next";
import { I18n } from "react-i18next";

class Navbar extends React.Component {
  render() {
    const { navigation } = this.props;
    return (
      <I18n>
        {" "}
        {t => (
          <nav>
            <ul>
              <li>
                <Link to="#location">
                  <i className="fas fa-map" />
                  <span className="description">Location</span>
                </Link>
              </li>
              <li>
                <Link to="#RSVP">
                  <i className="fas fa-envelope-open" />
                  <span className="description">
                    {i18n.t("navbar.RSVP", { framework: "react-i18next" })}
                  </span>
                </Link>
              </li>
              <li>
                <Link to="#hero">R & A</Link>
              </li>
              <li>
                <Link to="#aboutUs">
                  <i className="fas fa-edit" />
                  <span className="description">Contattaci</span>
                </Link>
              </li>
              <li>
                <Link to="#gifts">
                  <i className="fas fa-gift" />
                  <span className="description">Regali</span>
                </Link>
              </li>
            </ul>
          </nav>
        )}
      </I18n>
    );
  }
}

export default Navbar;
