import React from "react";
import "./Login.css";
import firebase from "firebase";
import base, { firebaseApp } from "../../base";

class Login extends React.Component {
  state = {
    isLoggedin: false
  };
  myUsernameInput = React.createRef();
  myEmailInput = React.createRef();
  myPasswordInput = React.createRef();

  authHandler = async authData => {
    const username = this.myUsernameInput.current.value;
    await base.post(`${username}/user`, {
      data: authData.user.email
    });
    // const RSVPform = await base.fetch()
  };

  authenticate = provider => {
    const authProvider = new firebase.auth.FacebookAuthProvider();
    firebaseApp
      .auth()
      .signInWithPopup(authProvider)
      .then(this.authHandler);
  };

  createNewUser = (email, password) => {
    firebaseApp
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .catch(function(error) {
        // Handle Errors here.
        var errorCode = error.code;
        var errorMessage = error.message;
        // ...
      });
  };

  loginUser = (email, password) => {
    firebaseApp
      .auth()
      .signInWithEmailAndPassword(email, password)
      .catch(function(error) {
        // Handle Errors here.
        var errorCode = error.code;
        var errorMessage = error.message;
        // ...
      });
  };

  submitLogin = () => {
    this.loginUser(
      this.myEmailInput.current.value,
      this.myPasswordInput.current.value
    );
    //redirect the user to his own page
    const username = this.myUsernameInput.current.value;
    this.props.history.push(`/user/${username}`);
  };

  signOut = () => {
    firebaseApp
      .auth()
      .signOut()
      .then(function() {
        // Sign-out successful.
      })
      .catch(function(error) {
        // An error happened.
      });
  };

  render() {
    return (
      <div className="loginContainer">
        <form className="login-form">
          <p>
            Accedi al sito del nostro matrimonio con i dati ricevuti via mail.
          </p>
          <div id="inputFields">
            <input
              onChange={this.handleChange}
              name="username"
              type="text"
              ref={this.myUsernameInput}
              placeholder="Username"
            />
            <input
              name="email"
              type="email"
              placeholder="Email address"
              ref={this.myEmailInput}
            />
            <input
              onChange={this.handleChange}
              name="password"
              type="password"
              placeholder="Password"
              ref={this.myPasswordInput}
            />
          </div>
          <p>
            Non ricordi i dati di accesso? Scrivici un messaggio{" "}
            <a href="mailto: ross.ferrandino@gmail.com"> qui</a>.
          </p>
          <button
            type="submit"
            onClick={() =>
              this.createNewUser(
                this.myEmailInput.current.value,
                this.myPasswordInput.current.value
              )
            }
          >
            Crea un nuovo user →{" "}
          </button>
          <button type="submit" onClick={() => this.submitLogin()}>
            Login →{" "}
          </button>
          <button type="submit" onClick={() => this.signOut()}>
            Sign out →{" "}
          </button>
          <button
            className="facebook"
            onClick={() => this.authenticate("Facebook")}
          >
            Login with Facebook
          </button>
        </form>
      </div>
    );
  }
}

export default Login;
