import React from "react";
import "./Login.css";
import firebase from "firebase";
import base, { firebaseApp } from "../../base";
import { translate, Trans } from "react-i18next";
import common_it from "../../translations/it/common.json";
import common_en from "../../translations/en/common.json";
import i18next from "i18next";

i18next.init({
  interpolation: { escapeValue: false }, // React already does escaping
  languages: ["en", "it"],
  lng: "en",
  resources: {
    en: {
      common: common_en // 'common' is our custom namespace
    },
    it: {
      common: common_it
    }
  }
});

class Login extends React.Component {
  //references to the input fields
  myUsernameInput = React.createRef();
  myEmailInput = React.createRef();
  myPasswordInput = React.createRef();

  // authHandler = async authData => {
  //   const username = this.myUsernameInput.current.value;
  //   await base.post(`${username}/user`, {
  //     data: authData.user.email
  //   });
  //   // const RSVPform = await base.fetch()
  // };
  //
  // authenticate = provider => {
  //   const authProvider = new firebase.auth.FacebookAuthProvider();
  //   firebaseApp
  //     .auth()
  //     .signInWithPopup(authProvider)
  //     .then(this.authHandler);
  // };

  handleSignUp = async event => {
    event.preventDefault();
    try {
      const user = await firebaseApp
        .auth()
        .createUserWithEmailAndPassword(
          this.myEmailInput.current.value,
          this.myPasswordInput.current.value
        );
      const username = this.myUsernameInput.current.value;
      this.props.history.push(`/user/${username}`);
    } catch (error) {
      alert(error);
    }
  };

  handleLogin = async event => {
    event.preventDefault();
    try {
      const user = await firebaseApp
        .auth()
        .signInWithEmailAndPassword(
          this.myEmailInput.current.value,
          this.myPasswordInput.current.value
        );
      //go to the RSVP page
      const username = this.myUsernameInput.current.value;
      this.props.history.push(`/user/${username}`);
    } catch (error) {
      alert(error);
    }
  };

  render() {
    const { t, i18n } = this.props;
    const changeLanguage = lng => {
      i18n.changeLanguage(lng);
    };
    return (
      <div className="loginContainer">
        <form className="login-form">
          <div>
            <button onClick={() => i18n.changeLanguage("it")}>it</button>
            <button onClick={() => i18n.changeLanguage("en")}>en</button>
          </div>
          <h1>
            {this.props.t("welcome.title", { framework: "react-i18next" })}
          </h1>
          <p>
            {" "}
            {this.props.t("welcome.intro", { framework: "react-i18next" })}
          </p>
          <div id="inputFields">
            <input
              onChange={this.handleChange}
              name="username"
              type="text"
              ref={this.myUsernameInput}
              placeholder="Username"
            />
            <input
              name="email"
              type="email"
              placeholder="Email address"
              ref={this.myEmailInput}
            />
            <input
              onChange={this.handleChange}
              name="password"
              type="password"
              placeholder="Password"
              ref={this.myPasswordInput}
            />
          </div>
          <p>
            Non ricordi i dati di accesso? Scrivici un messaggio{" "}
            <a href="mailto: ross.ferrandino@gmail.com"> qui</a>.
          </p>
          <button type="submit" onClick={this.handleSignUp}>
            Crea un nuovo user →{" "}
          </button>
          <button type="submit" onClick={this.handleLogin}>
            Login →{" "}
          </button>
        </form>
      </div>
    );
  }
}

export default translate("common")(Login);
