import React from "react";
import "./RSVPform.css";

class RSVPform extends React.Component {
  myInput = React.createRef();
  submitForm = e => {
    //stop the form from submitting
    e.preventDefault();
    //getting the input value of the submitForm
    console.log(this.myInput.current.value);
  };
  render() {
    return (
      <div className="register-wrapper">
        <div className="register-block">
          <h3 className="register-title">Conferma presenza</h3>
          <form onSubmit={this.submitForm}>
            <input
              required
              name="name"
              type="text"
              ref={this.myInput}
              placeholder="Nome"
            />
            <input
              required
              name="email"
              type="email"
              placeholder="Indirizzo email"
            />
            <input name="guests" type="number" placeholder="Numero di ospiti" />
            <select name="attendance" required>
              <option value="attending">Ci sono!</option>
              <option value="not_attending">Me lo perdo</option>
            </select>
            <textarea name="allergies" placeholder="Intolleranze alimentari" />
            <textarea name="music" placeholder="Suggerimenti musica" />
            <input type="submit" value="Conferma" />
          </form>
        </div>
      </div>
    );
  }
}

export default RSVPform;
