import React from "react";

class PlusOne extends React.Component {
  state = {
    radioButtonId: null
  };

  setResponse = e => {
    e.preventDefault();
    console.log(e.target.value);
  };

  render() {
    return (
      <div onChange={() => this.setResponse()}>
        <input
          name="plusOne"
          type="radio"
          value="yes"
          onChange={this.handleChange}
        />
        <input
          name="plusOne"
          type="radio"
          value="no"
          onChange={this.handleChange}
        />
      </div>
    );
  }
}

export default PlusOne;
