import React from "react";
// import "../RSVPform/RSVPform.css";
import "./EditRSVPform.css";
import i18next from "../i18next";

class EditRSVPform extends React.Component {
  handleChange = e => {
    //Taking a copy of the current RSVP
    const updatedRSVP = {
      ...this.props.completedForm.RSVP,
      [e.currentTarget.name]: e.currentTarget.value
    };
    this.props.updateRSVP("RSVP", updatedRSVP);
  };

  //displaying the correct information in the input fields
  getFieldValues = fieldName => {
    if (this.props.completedForm.user === this.props.user) {
      let individualRSVP = this.props.completedForm.RSVP;
      for (let params in individualRSVP) {
        if (individualRSVP.hasOwnProperty(params) && params === fieldName) {
          return individualRSVP[fieldName];
        }
      }
    }
  };

  render() {
    return (
      <div id="RSVP">
        <div>
          <h3 className="register-title">
            {this.props.t("RSVP.header", {
              framework: "react-i18next"
            })}
          </h3>
          <p>
            {this.props.t("RSVP.thanks", {
              framework: "react-i18next"
            })}
          </p>
          <form className="myForm">
            <ul>
              <li>
                <label for="name">
                  {this.props.t("RSVP.fieldName", {
                    framework: "react-i18next"
                  })}
                </label>
                <input
                  disabled
                  // onChange={this.handleChange}
                  name="name"
                  type="text"
                  // placeholder={this.props.t("RSVP.fieldName", {
                  //   framework: "react-i18next"
                  // })}
                  value={this.getFieldValues("name")}
                />
                <span>Inserisci il tuo nome e cognome</span>
              </li>
              <li>
                <label for="email">
                  {this.props.t("loginpage.placeholderEmail", {
                    framework: "react-i18next"
                  })}
                </label>
                <input
                  disabled
                  // onChange={this.handleChange}
                  name="email"
                  type="email"
                  // placeholder={this.props.t("loginpage.placeholderEmail", {
                  //   framework: "react-i18next"
                  // })}
                  value={this.getFieldValues("email")}
                />
                <span>Inserisci la tua mail</span>
              </li>
              <li>
                <label for="postalAddress">
                  {this.props.t("RSVP.fieldAddress", {
                    framework: "react-i18next"
                  })}
                </label>
                <input
                  // onChange={this.handleChange}
                  disabled
                  name="postalAddress"
                  type="text"
                  // placeholder={this.props.t("RSVP.fieldAddress", {
                  //   framework: "react-i18next"
                  // })}
                  value={this.getFieldValues("postalAddress")}
                />
                <span>Your postal address for our official invitation</span>
              </li>
              <li>
                <label for="attendance">Attendance</label>
                <select
                  disabled
                  // onChange={this.handleChange}
                  name="attendance"
                >
                  <option value="yes">
                    {this.props.t("RSVP.fieldAttendanceYes", {
                      framework: "react-i18next"
                    })}
                    !
                  </option>
                  <option value="no">
                    {this.props.t("RSVP.fieldAttendanceNo", {
                      framework: "react-i18next"
                    })}
                  </option>
                </select>
                <span>Can you make it?</span>
              </li>
              <li>
                <label for="allergies">Dietary preferences</label>
                <textarea
                  disabled
                  // onChange={this.handleChange}
                  name="allergies"
                  placeholder={this.props.t("RSVP.fieldAllergies", {
                    framework: "react-i18next"
                  })}
                  value={this.getFieldValues("allergies")}
                />
                <span>
                  Let us know if you have any allergies or dietary preferences
                </span>
              </li>
              <textarea
                disabled
                // onChange={this.handleChange}
                name="music"
                placeholder={this.props.t("RSVP.fieldMusic", {
                  framework: "react-i18next"
                })}
                value={this.getFieldValues("music")}
              />
              <div className="guestDetails">
                <p>
                  {this.props.t("plusOne.headerEdit", {
                    framework: "react-i18next"
                  })}
                </p>
                <input
                  disabled
                  name="guestName"
                  type="text"
                  placeholder={this.props.t("RSVP.fieldName", {
                    framework: "react-i18next"
                  })}
                  // onChange={this.handleChange}
                  value={this.getFieldValues("guestName")}
                />
                <textarea
                  disabled
                  name="guestAllergies"
                  // onChange={this.handleChange}
                  placeholder={this.props.t("plusOne.guestAllergies", {
                    framework: "react-i18next"
                  })}
                  value={this.getFieldValues("guestAllergies")}
                />
              </div>
            </ul>
          </form>
        </div>
      </div>
    );
  }
}

export default EditRSVPform;
