  //   const email = this.myEmailInput.current.value;
  //   const password: this.myPasswordInput.current.value;
  //   const username: this.myUsernameInput.current.value;
  //   firebaseApp.auth(fetchProvidersForEmail(email).then(provider) => {
  //
  //   })
  // };
  // login = e => {
  //   e.preventDefault();
  //   base
  //     .auth()
  //     .signInWithEmailAndPassword(this.state.email, this.state.password)
  //     .then(u => {})
  //     .catch(error => {
  //       console.log(error);
  //     });
  // };

  createNewAccount = (email, password) => {
    firebaseApp
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .catch(function(error) {
        // Handle Errors here.
        var errorCode = error.code;
        var errorMessage = error.message;
        // ...
      });
    this.setState({ email, password });
  };

  // authListener = () => {
  //   base.auth().onAuthStateChanged(user => {
  //     // console.log(user);
  //     if (user) {
  //       this.setState({ user });
  //       // localStorage.setItem("user", user.uid);
  //     } else {
  //       this.setState({ user: null });
  //       // localStorage.removeItem("user");
  //     }
  //   });
  // };
  //
  // componentDidMount() {
  //   this.authListener();
  // }

  myUsernameInput = React.createRef();
  myEmailInput = React.createRef();
  myPasswordInput = React.createRef();

  goToUser = () => {
    const username = this.myUsernameInput.current.value;
    this.props.history.push(`/user/${username}`);
  };

  // handleChange = e => {
  //   this.setState({ [e.target.name]: e.target.value });
  // };

  render() {
    if (this.state.redirect === true) {
      return <Redirect to="/user/rossella" />;
    }
    return (
      <div className="loginContainer">
        <form
          onSubmit={event => {
            this.authWithEmailPassword(event);
          }}
          ref={form => {
            this.loginForm = form;
          }}
          className="login-form"
        >
          <p>
            Accedi al sito del nostro matrimonio con i dati ricevuti via mail.
          </p>
          <div id="inputFields">
            <input
              onChange={this.handleChange}
              name="username"
              type="text"
              ref={this.myUsernameInput}
              required
              placeholder="Username"
              value={"Rossella"}
            />

            <input
              name="email"
              type="email"
              placeholder="Email address"
              ref={this.myEmailInput}
            />
            <input
              onChange={this.handleChange}
              name="password"
              type="password"
              placeholder="Password"
              ref={this.myPasswordInput}
            />
          </div>
          <p>
            Non ricordi i dati di accesso? Scrivici un messaggio{" "}
            <a href="mailto: ross.ferrandino@gmail.com"> qui</a>.
          </p>
          <button type="submit">Accedi al sito → </button>
          <button type="submit">Registrati →</button>
        </form>
      </div>
    );
  }
}

export default LoginFirebase;
