import React from "react";
import "./Navbar.css";

//handling the navigation
import { HashLink as Link } from "react-router-hash-link";

//handling the translations
import common_it from "../../translations/it/common.json";
import common_en from "../../translations/en/common.json";
import i18next from "i18next";
import LngDetector from "i18next-browser-languagedetector";

i18next.use(LngDetector).init({
  interpolation: { escapeValue: false }, // React already does escaping
  languages: ["en", "it"],
  resources: {
    en: {
      common: common_en // 'common' is our custom namespace
    },
    it: {
      common: common_it
    }
  }
});

class Navbar extends React.Component {
  render() {
    const { t, i18n } = this.props;
    console.log(this.props);
    const changeLanguage = lng => {
      i18n.changeLanguage(lng);
    };
    console.log(this);
    return (
      <nav>
        <ul>
          <li>
            <Link to="#location">
              <i className="fas fa-map" />
              <span className="description">Location</span>
            </Link>
          </li>
          <li>
            <Link to="#RSVP">
              <i className="fas fa-envelope-open" />
              <span className="description">
                {this.props.t("navbar.RSVP", { framework: "react-i18next" })}
              </span>
            </Link>
          </li>
          <li>
            <Link to="#hero">R & A</Link>
          </li>
          <li>
            <Link to="#aboutUs">
              <i className="fas fa-edit" />
              <span className="description">Contattaci</span>
            </Link>
          </li>
          <li>
            <Link to="#gifts">
              <i className="fas fa-gift" />
              <span className="description">Regali</span>
            </Link>
          </li>
        </ul>
      </nav>
    );
  }
}

export default Navbar;
