import React from "react";
import "./Hero.css";
// import Countdown from "../Countdown/Countdown";

class Hero extends React.Component {
  render() {
    return (
      <section className="header" id="hero">
        {/*}<Countdown />*/}
        <div id="tagline">
          <h1>Ross & Rachel</h1>
          <h3>
            {this.props.t("hero.weddingDate", {
              framework: "react-i18next"
            })}
          </h3>
          <h3>Central Perk Cafe, NY</h3>
        </div>
      </section>
    );
  }
}

export default Hero;
