import React from "react";
import "../RSVPform/RSVPform.css";

class EditRSVPform extends React.Component {
  // nameRef = React.createRef();
  // emailRef = React.createRef();
  // guestsRef = React.createRef();
  // attendanceRef = React.createRef();
  // allergiesRef = React.createRef();
  // musicRef = React.createRef();
  //
  // submitForm = e => {
  //   //stop the form from submitting
  //   e.preventDefault();
  //
  //   //getting the input value of the submitForm
  //   const RSVP = {
  //     name: this.nameRef.current.value,
  //     email: this.emailRef.current.value,
  //     guests: this.guestsRef.current.value,
  //     attendance: this.attendanceRef.current.value,
  //     allergies: this.allergiesRef.current.value,
  //     music: this.musicRef.current.value
  //   };
  //
  //   this.props.addRSVP(RSVP);
  //   //refresh the submitForm
  //   e.currentTarget.reset();
  // };
  //
  // renderForm() {
  //   if (this.props.completedForm) {
  //   }
  // }

  handleChange = e => {
    console.log(e.currentTarget.value);
    const updatedRSVP = { ...this.props.completedForm };
    console.log(updatedRSVP);
  };

  render() {
    return (
      <div className="register-wrapper">
        <div className="register-block">
          <h3 className="register-title">Conferma presenza</h3>
          <form onSubmit={this.submitForm}>
            <input
              required
              onChange={this.handleChange}
              name="name"
              type="text"
              placeholder="Nome"
              value={this.props.completedForm.name}
            />
            <input
              required
              onChange={this.handleChange}
              name="email"
              type="email"
              placeholder="Indirizzo email"
              value={this.props.completedForm.email}
            />
            <input
              onChange={this.handleChange}
              name="guests"
              type="number"
              placeholder="Numero di ospiti"
              value={this.props.completedForm.guests}
            />
            <select
              required
              onChange={this.handleChange}
              name="attendance"
              value={this.props.completedForm.attendance}
            >
              <option value="yes">Ci sono!</option>
              <option value="no">Me lo perdo</option>
            </select>
            <textarea
              onChange={this.handleChange}
              name="allergies"
              value={this.props.completedForm.allergies}
              placeholder="Intolleranze alimentari"
            />
            <textarea
              onChange={this.handleChange}
              name="music"
              value={this.props.completedForm.music}
              placeholder="Suggerimenti musica"
            />
            <input type="submit" value="Conferma" />
          </form>
        </div>
      </div>
    );
  }
}

export default EditRSVPform;
