import React from "react";
import { Map, InfoWindow, Marker, GoogleApiWrapper } from "google-maps-react";

const myApiKey = "AIzaSyCNn_ezyRyN-r88ZLhixrorglnHwu1ATng";

export class MyMap extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showInfoWindow: false
    };
  }

  onMarkerClick = () => {
    this.setState({ showInfoWindow: true });
  };

  onMapClicked = () => {
    this.setState({ showInfoWindow: false });
  };

  render() {
    const style = {
      maxWidth: "50%",
      maxHeight: "50%",
      margin: "10px",
      border: "2px solid rgb(63,81,181,0.4)",
      display: "flex"
    };

    return (
      <div>
        <Map
          style={style}
          google={this.props.google}
          zoom={12}
          initialCenter={{
            lat: 41.451774,
            lng: 15.616402
          }}
        >
          <Marker
            title={"The wedding location"}
            name={"Tenimento San Giuseppe"}
            position={{ lat: 41.451774, lng: 15.616402 }}
          />
          <Marker
            title={"Areoporto di Bari"}
            name={"Areoporto di Bari"}
            position={{ lat: 41.137799, lng: 16.765148 }}
          />
          <Marker onClick={this.onMarkerClick} name={"Current location"} />
        </Map>
      </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: myApiKey
})(MyMap);
