import React from "react";

class Countdown extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      days: 0,
      hours: 0,
      minutes: 0,
      seconds: 0
    };
  }

  weddingTime = new Date(2019, 4, 26, 12, 0);

  componentWillMount() {
    this.getTimeUntil();
  }

  componentDidMount() {
    setInterval(() => this.getTimeUntil(), 1000);
  }

  getTimeUntil = () => {
    const currentTime = new Date();
    const weddingTime = new Date(2019, 4, 26, 12, 0);
    const timeUntil = weddingTime - currentTime;
    console.log(timeUntil);

    // if (timeUntil < 0) {
    //   this.setState({ days: 0, hours: 0, minutes: 0, seconds: 0 });
    // } else {
    const seconds = Math.floor(timeUntil / 1000);
    const minutes = Math.floor((timeUntil / 1000 / 60) % 60);
    const hours = Math.floor((timeUntil / (1000 * 60 * 60)) % 24);
    const days = Math.floor(timeUntil / (1000 * 60 * 60 * 24));
    this.setState({ days, hours, minutes, seconds });
    // }
  };

  render() {
    return (
      <div>
        <p>
          {this.state.days} days, {this.state.hours} hours,
          {this.state.minutes} minutes, {this.state.seconds} seconds
        </p>
        <p>until I do</p>
      </div>
    );
  }
}

export default Countdown;
