import React from "react";

class PlusOne extends React.Component {
  state = {
    isChecked: false
  };
  isAttending = e => {
    e.preventDefault();
    if (e.target.value === "yes") {
      return true;
    } else {
      return false;
    }
  };

  render() {
    return (
      <div>
        <ul onChange={e => this.isAttending(e)}>
          <li>
            <label>Si</label>
            <input
              name="plusOne"
              type="radio"
              value="yes"
              onChange={this.handleChange}
              onClick={() => this.setState({ isChecked: true, plusOne: true })}
            />
          </li>
          <li>
            <label>No</label>
            <input
              name="plusOne"
              type="radio"
              value="no"
              onClick={() => this.setState({ isChecked: true, plusOne: false })}
              onChange={this.handleChange}
            />
          </li>
        </ul>
      </div>
    );
  }
}

export default PlusOne;
