import React from "react";
import "./RSVPform.css";

class RSVPform extends React.Component {
  myInput = React.createRef();
  submitForm(e) {
    e.preventDefault();
    //stop the form from submitting
    //getting the input value of the submitForm
  }
  render() {
    return (
      <div className="register-wrapper">
        <div className="register-block">
          <h3 className="register-title">Conferma presenza</h3>
          <form onSubmit={this.submitForm}>
            <input required type="text" ref={this.myInput} placeholder="Nome" />
            <input required type="email" placeholder="Indirizzo email" />
            <input required type="number" placeholder="Numero di ospiti" />
            <input type="textarea" placeholder="Intolleranze alimentari" />
            <input type="textarea" placeholder="Suggerimenti musica" />
            <input type="submit" value="Conferma" />
          </form>
        </div>
      </div>
    );
  }
}

export default RSVPform;
