import React from "react";
import "./AboutUs.css";
//handling the translations
import i18next from "../i18next";

const AboutUs = () => {
  return (
    <div id="aboutUs">
      <h3 className="contactus">
        {this.props.t("contactus.title", {
          framework: "react-i18next"
        })}
      </h3>
      <p className="moreinfo">
        {this.props.t("contactus.summary", {
          framework: "react-i18next"
        })}{" "}
        <a href="mailto:nottheusualtourists@gmail.com">qui </a> o mandaci un
        {this.props.t("contactus.summary2", {
          framework: "react-i18next"
        })}
      </p>
      <hr />
      <div id="brideandgroom">
        <div id="bride">
          <img
            className="profilePicture"
            src="/images/profilepicture-crop.jpg"
          />
          <p className="moreinfo">
            Nata e cresciuta a Foggia, nella splendida Puglia, ma con il
            costante desiderio di viaggiare e scoprire posti e culture nuove.
            Decido di iscrivermi all'universit√† Ca' Foscari di Venezia, dove mi
            laureo in Lingua Cinese e conosco Antonio.
          </p>
        </div>
        <div id="groom">
          <img className="profilePicture" src="/images/Antonio-crop.JPG" />
          <p className="moreinfo">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim
            ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut
            aliquip ex ea commodo consequat.
          </p>
        </div>
      </div>
    </div>
  );
};

export default AboutUs;
