import React from "react";

class Map extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      mapLocations: [
        {
          name: "Tenimento San Giuseppe",
          title: "Tenimento San Giuseppe",
          position: { lat: 41.451774, lng: 15.616402 }
        },
        {
          name: "Areoporto di Bari",
          title: "Areoporto di Bari",
          position: { lat: 41.137799, lng: 16.765148 }
        },
        {
          name: "Hotel Up Spa",
          title: "Hotel Up Spa",
          position: { lat: 41.462855, lng: 15.551417 }
        }
      ]
    };
  }

  //lifecycle event to call the render map function
  componentDidMount() {
    this.loadMap();
  }
  //load the script
  loadMap = () => {
    loadScript(
      "https://maps.googleapis.com/maps/api/js?key=AIzaSyCNn_ezyRyN-r88ZLhixrorglnHwu1ATng&callback=initMap"
    );
    window.initMap = this.initMap;
  };

  initMap = () => {
    const map = new window.google.maps.Map(document.getElementById("map"), {
      center: { lat: 41.451774, lng: 15.616402 },
      zoom: 8
    });

    this.state.mapLocations.map(location => {
      const contentString = `$(location.name)`;
      const infowindow = new window.google.maps.InfoWindow({
        content: contentString
      });

      const marker = new window.google.maps.Marker({
        position: { lat: location.position.lat, lng: location.position.lng },
        map: map,
        title: location.title
      });
    });
  };

  render() {
    const style = {
      width: "600px",
      height: "400px",
      margin: "10px",
      border: "2px solid rgb(63,81,181,0.4)",
      margin: "0 auto"
    };
    return (
      <main>
        <div style={style} id="map" />
      </main>
    );
  }
}

//Creating the script to access Google Maps API
function loadScript(url) {
  var index = window.document.getElementsByTagName("script")[0];
  var script = window.document.createElement("script");
  script.src = url;
  script.async = true;
  script.defer = true;
  index.parentNode.insertBefore(script, index);
}

export default Map;
