import React from "react";
import { Map, InfoWindow, Marker, GoogleApiWrapper } from "google-maps-react";

const myApiKey = "AIzaSyCNn_ezyRyN-r88ZLhixrorglnHwu1ATng";

export class MyMap extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showInfoWindow: false,
      activeMarker: {},
      selectedPlace: {}
    };
  }

  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onMapClicked = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  render() {
    const style = {
      maxWidth: "60%",
      maxHeight: "60%",
      margin: "10px",
      border: "2px solid rgb(63,81,181,0.4)",
      margin: "0 auto"
    };

    return (
      <div
        style={{
          height: "60%",
          width: "60%",
          display: "flex",
          flexFlow: "row nowrap",
          justifyContent: "center",
          padding: 0
        }}
      >
        <Map
          style={style}
          onClick={this.onMapClicked}
          google={this.props.google}
          zoom={7}
          initialCenter={{
            lat: 41.451774,
            lng: 15.616402
          }}
        >
          <Marker
            onClick={this.onMarkerClick}
            name={"Tenimento San Giuseppe"}
            position={{ lat: 41.451774, lng: 15.616402 }}
          />
          <Marker
            onClick={this.onMarkerClick}
            name={"Areoporto di Bari"}
            position={{ lat: 41.137799, lng: 16.765148 }}
          />
          <InfoWindow
            marker={this.state.activeMarker}
            visible={this.state.showInfoWindow}
          >
            <div>
              <h1>{this.state.selectedPlace.name}</h1>
            </div>
          </InfoWindow>
        </Map>
      </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: myApiKey,
  language: "it",
  region: "IT"
})(MyMap);
