import React from "react";
import Map from "../Map/Map";
import "./Location.css";
import i18next from "../i18next";

class Location extends React.Component {
  render() {
    return (
      <div className="location" id="location">
        <h3>Location</h3>
        <p>
          {this.props.t("location.introduction", {
            framework: "react-i18next"
          })}
        </p>
        <hr />
        <ul className="image_grid">
          <li className="small" />
          <li className="large" />
          <li className="large" />
          <li className="small" />
        </ul>
        <p>
          {this.props.t("location.cerimonia", {
            framework: "react-i18next"
          })}{" "}
          <a
            target="_blank"
            rel="noopener noreferrer"
            href="https://tenimentosangiuseppe.com"
          >
            Tenimento San Giuseppe
          </a>
          {this.props.t("location.Tenimento", {
            framework: "react-i18next"
          })}
        </p>
        <p>
          {" "}
          {this.props.t("location.address", {
            framework: "react-i18next"
          })}
          : Via Trinitapoli km 4, 71121 Foggia FG, Italy
        </p>
        <p>
          {this.props.t("location.mapsuggestions", {
            framework: "react-i18next"
          })}
        </p>
        <Map />
        <div id="directions">
          <div className="transportList">
            <h2>
              <i className="fas fa-plane" />
              {this.props.t("location.plane", {
                framework: "react-i18next"
              })}
            </h2>
            <span className="detailedDirections">
              {this.props.t("location.planedescription1", {
                framework: "react-i18next"
              })}
              <a
                target="_blank"
                rel="noopener noreferrer"
                href="https://www.busradar.com/coach/bari-airport-bri/foggia/"
              >
                {this.props.t("location.planedescription2", {
                  framework: "react-i18next"
                })}
              </a>{" "}
              {this.props.t("location.planedescription3", {
                framework: "react-i18next"
              })}
            </span>
          </div>
          <div className="transportList">
            <h2>
              <i className="fas fa-car" />
              In macchina
            </h2>
            <span className="detailedDirections">
              Se arrivate dalla A14 Bologna-Taranto, potete uscire direttamente
              dall'uscita Foggia. Dalla A1 Roma-Napoli, dall'autostrada A16
              Napoli-Canosa, uscita Candela, poi prendere la superstrada SS655
              Candela-Foggia, direzione Foggia.
            </span>
          </div>
          <div className="transportList">
            <h2>
              <i className="fas fa-train" />
              In treno
            </h2>
            <span className="detailedDirections">
              Foggia è servita da un gran numero di treni a lunga distanza
              (Trenitalia) nonché da una fitta rete di treni regionali. Da Roma,
              Napoli o Bari si può prendere il treno per Foggia, cosi come dai
              treni regionali diretti a Potenza, con cambio ad Ascoli Satriano.
            </span>
          </div>
        </div>
      </div>
    );
  }
}

export default Location;
