import React from "react";
import "./Login.css";
import firebase from "firebase";
import base, { firebaseApp } from "../../base";

class Login extends React.Component {
  state = {
    isAuthenticated: false
  };

  //references to the input fields
  myUsernameInput = React.createRef();
  myEmailInput = React.createRef();
  myPasswordInput = React.createRef();

  authHandler = async authData => {
    const username = this.myUsernameInput.current.value;
    await base.post(`${username}/user`, {
      data: authData.user.email
    });
    // const RSVPform = await base.fetch()
  };

  authenticate = provider => {
    const authProvider = new firebase.auth.FacebookAuthProvider();
    firebaseApp
      .auth()
      .signInWithPopup(authProvider)
      .then(this.authHandler);
  };

  handleSignUp = async event => {
    event.preventDefault();
    try {
      const user = await firebaseApp
        .auth()
        .createUserWithEmailAndPassword(
          this.myEmailInput.current.value,
          this.myPasswordInput.current.value
        );
      const username = this.myUsernameInput.current.value;
      this.props.history.push(`/user/${username}`);
    } catch (error) {
      alert(error);
    }
  };

  handleLogin = async event => {
    event.preventDefault();
    try {
      const user = await firebaseApp
        .auth()
        .signInWithEmailAndPassword(
          this.myEmailInput.current.value,
          this.myPasswordInput.current.value
        )
        .then(this.setState({ isAuthenticated: true }));
      if (this.state.isAuthenticated) {
        const username = this.myUsernameInput.current.value;
        this.props.history.push(`/user/${username}`);
      } else {
        alert("Login first");
      }
    } catch (error) {
      alert(error);
    }
  };

  // createNewUser = (email, password) => {
  //   firebaseApp
  //     .auth()
  //     .createUserWithEmailAndPassword(email, password)
  //     .catch(function(error) {
  //       // Handle Errors here.
  //       alert(error);
  //       var errorCode = error.code;
  //       var errorMessage = error.message;
  //       // ...
  //     });
  // };

  // loginUser = (email, password) => {
  //   firebaseApp
  //     .auth()
  //     .signInWithEmailAndPassword(email, password)
  //     .catch(function(error) {
  //       alert(error);
  //       // Handle Errors here.
  //       var errorCode = error.code;
  //       var errorMessage = error.message;
  //       // ...
  //     });
  // };
  //
  // submitLogin = () => {
  //   this.loginUser(
  //     this.myEmailInput.current.value,
  //     this.myPasswordInput.current.value
  //   );
  //   if (this.state.isAuthenticated) {
  //     //redirect the user to his own page
  //     const username = this.myUsernameInput.current.value;
  //     this.props.history.push(`/user/${username}`);
  //   }
  // };

  signOut = () => {
    firebaseApp
      .auth()
      .signOut()
      .then(function() {
        // Sign-out successful.
      })
      .catch(function(error) {
        // An error happened.
      });
  };

  render() {
    return (
      <div className="loginContainer">
        <form className="login-form">
          <p>
            Accedi al sito del nostro matrimonio con i dati ricevuti via mail.
          </p>
          <div id="inputFields">
            <input
              onChange={this.handleChange}
              name="username"
              type="text"
              ref={this.myUsernameInput}
              placeholder="Username"
            />
            <input
              name="email"
              type="email"
              placeholder="Email address"
              ref={this.myEmailInput}
            />
            <input
              onChange={this.handleChange}
              name="password"
              type="password"
              placeholder="Password"
              ref={this.myPasswordInput}
            />
          </div>
          <p>
            Non ricordi i dati di accesso? Scrivici un messaggio{" "}
            <a href="mailto: ross.ferrandino@gmail.com"> qui</a>.
          </p>
          <button type="submit" onClick={this.handleSignUp}>
            Crea un nuovo user →{" "}
          </button>
          <button type="submit" onClick={this.handleLogin}>
            Login →{" "}
          </button>
          <button type="submit" onClick={() => this.signOut()}>
            Sign out →{" "}
          </button>
          <button
            className="facebook"
            onClick={() => this.authenticate("Facebook")}
          >
            Login with Facebook
          </button>
        </form>
      </div>
    );
  }
}

export default Login;
