import Rebase from "re-base";
import firebase from "firebase";

const firebaseApp = firebase.initializeApp({
  apiKey: "AIzaSyCHe5qQi8_FybadQ9qY6Qe8SnwD1bz32xQ",
  authDomain: "ra-wedding.firebaseapp.com",
  databaseURL: "https://ra-wedding.firebaseio.com",
  projectId: "ra-wedding"
  // storageBucket: "ra-wedding.appspot.com",
  // messagingSenderId: "841290425009"
});

//created the firebase binding
const base = Rebase.createClass(firebaseApp.database());

export { firebaseApp };

export default base;
