import React from "react";

class PlusOne extends React.Component {
  state = {
    radioButtonId: null
  };

  setResponse = e => {
    e.preventDefault();
    console.log(e.target.value);
  };

  render() {
    return (
      <div>
        <ul onClick={event => this.setResponse(event)}>
          <li>
            <label>Si</label>
            <input
              name="plusOne"
              type="radio"
              value="yes"
              onChange={this.handleChange}
            />
          </li>
          <li>
            <label>No</label>
            <input
              name="plusOne"
              type="radio"
              value="no"
              onChange={this.handleChange}
            />
          </li>
        </ul>
      </div>
    );
  }
}

export default PlusOne;
