import React, { Component } from "react";
import "./App.css";
import Navbar from "./components/Navbar/Navbar";
import Hero from "./components/Hero/Hero";
import Login from "./components/Login/Login";
import Location from "./components/Location/Location";
import RSVPform from "./components/RSVPform/RSVPform";
import initialData from "./components/loadDatabase";
import base from "./base";

class App extends Component {
  state = {
    RSVPs: {},
    locations: {},
    username: "",
    isLoggedin: true
  };

  // authListener = () => {
  //   base.auth().onAuthStateChanged(user => {
  //     // console.log(user);
  //     if (user) {
  //       this.setState({ user });
  //       // localStorage.setItem("user", user.uid);
  //     } else {
  //       this.setState({ user: null });
  //       // localStorage.removeItem("user");
  //     }
  //   });
  // };

  componentDidMount() {
    const { params } = this.props.match;
    //sostituire il path per la submission su firebase con lo username
    this.ref = base.syncState(`${params.userId}/user`, {
      context: this,
      state: "RSVPs"
    });
    // this.authListener();
  }

  //unlistening to changes once the user goes back to the previous page
  componentWillUnmount() {
    base.removeBinding(this.ref);
  }

  addRSVP = RSVP => {
    //take a copy of the existing state using an object spread
    const RSVPs = { ...this.state.RSVPs };
    //add our new RSVP form to the RSVPs variable
    RSVPs[`RSVP`] = RSVP;
    //set the new RSVP object to state
    this.setState({ RSVPs });
  };

  loadData = () => {
    this.setState({
      RSVPs: initialData
    });
  };

  updateRSVP = (key, updatedRSVP) => {
    //take a copy of the current RSVP
    const RSVPs = { ...this.state.RSVPs };
    //update that state
    RSVPs[key] = updatedRSVP;
    //set this to state
    this.setState({ RSVPs });
  };

  addUser = user => {
    const username = { ...this.state.username };
    this.setState({ username: user });
  };

  renderApp = () => {
    if (this.state.isLoggedin) {
      return (
        <div>
          <div className="hero_panel">
            <Hero />
            <Navbar />
          </div>
          <Location />
          <RSVPform
            addRSVP={this.addRSVP}
            loadData={this.loadData}
            completedForm={this.state.RSVPs}
            updateRSVP={this.updateRSVP}
            key={this.state.key}
          />
        </div>
      );
    }
    return <Login addUser={this.addUser} />;
  };

  render() {
    <div>{this.renderApp()}</div>;
  }
}

export default App;
