  myUsernameInput = React.createRef();
  myEmailInput = React.createRef();
  myPasswordInput = React.createRef();

  //if the user is authenticated, run this method
  goToUser = e => {
    e.preventDefault();
    const username = this.myInput.current.value;
    if (this.state.authenticated === true) {
      this.props.history.push(`/user/${username}`);
    } else {
      alert("Please enter your username and password to login");
    }
  };

  // handleChange = e => {
  //   this.setState({ [e.target.name]: e.target.value });
  // };

  render() {
    return (
      <div className="loginContainer">
        <form onSubmit={this.goToUser} className="login-form">
          <p>
            Accedi al sito del nostro matrimonio con i dati ricevuti via mail.
          </p>
          <div id="inputFields">
            <input
              onChange={this.handleChange}
              name="username"
              type="text"
              ref={this.myUsernameInput}
              required
              placeholder="Username"
            />
            <input
              name="email"
              type="email"
              placeholder="Email address"
              ref={this.myEmailInput}
            />
            <input
              onChange={this.handleChange}
              name="password"
              type="password"
              placeholder="Password"
              ref={this.myPasswordInput}
            />
          </div>
          <p>
            Non ricordi i dati di accesso? Scrivici un messaggio{" "}
            <a href="mailto: ross.ferrandino@gmail.com"> qui</a>.
          </p>
          <button type="submit">Accedi al sito → </button>
          <button type="submit">
            Registrati → onClick=
            {this.createNewAccount(this.myEmailInput, this.myPasswordInput)}
          </button>
        </form>
      </div>
    );
  }
}

export default Login;
