import React from "react";
import "./Location.css";

class Location extends React.Component {
  render() {
    return (
      <div className="location">
        <div id="title">
          <h2>Location</h2>
          <h4>Qualche informazione in più su dove ci sposeremo.</h4>
        </div>
        <img src="/images/tenimento1.jpg" />
        <p>
          La cerimonia e il ricevimento si svolgeranno entrambi a Tenimento San
          Giuseppe. Si tratta di un'antica masseria e dimora storica della
          provincia di Foggia, città natale di Rossella. L'antica masserie è
          circondata da un parco di 20mila metri quadrati, e da un giardino che
          offre un'incantevole varietà di piante mediterranee, inclusi i nostri
          amati ulivi.
        </p>
        <p>Indirizzo: Via Trinitapoli km 4, Foggia, 71122</p>
      </div>
    );
  }
}

export default Location;
