//package that mirrors to state
import Rebase from "re-base";
import firebase from "firebase";

const firebaseApp = firebase.initializeApp({
  apiKey: "AIzaSyC2NLA-eRbvzzoCCs1UrHcMHFO9CMwdNPg",
  authDomain: "wedding-demo-9ff33.firebaseapp.com",
  databaseURL: "https://wedding-demo-9ff33.firebaseio.com",
  projectId: "wedding-demo-9ff33",
  storageBucket: "wedding-demo-9ff33.appspot.com",
  messagingSenderId: "525749240677"
});

//created the firebase binding
const base = Rebase.createClass(firebaseApp.database());

export { firebaseApp };

export default base;
