import React from "react";
import "./Login.css";
//connecting to the database
import firebase from "firebase";
import base, { firebaseApp } from "../../base";
import { translate, Trans } from "react-i18next";

//handling the translations
import i18next from "../i18next";

class Login extends React.Component {
  //references to the input fields
  myUsernameInput = React.createRef();
  myEmailInput = React.createRef();
  myPasswordInput = React.createRef();

  // authHandler = async authData => {
  //   const username = this.myUsernameInput.current.value;
  //   await base.post(`${username}/user`, {
  //     data: authData.user.email
  //   });
  //   // const RSVPform = await base.fetch()
  // };
  //
  // authenticate = provider => {
  //   const authProvider = new firebase.auth.FacebookAuthProvider();
  //   firebaseApp
  //     .auth()
  //     .signInWithPopup(authProvider)
  //     .then(this.authHandler);
  // };

  handleSignUp = async event => {
    event.preventDefault();
    try {
      const user = await firebaseApp
        .auth()
        .createUserWithEmailAndPassword(
          this.myEmailInput.current.value,
          this.myPasswordInput.current.value
        );
      const username = this.myUsernameInput.current.value;
      this.props.history.push(`/user/${username}`);
    } catch (error) {
      alert(error);
    }
  };

  handleLogin = async event => {
    event.preventDefault();
    try {
      const user = await firebaseApp
        .auth()
        .signInWithEmailAndPassword(
          this.myEmailInput.current.value,
          this.myPasswordInput.current.value
        );
      //go to the RSVP page
      const username = this.myUsernameInput.current.value;
      this.props.history.push(`/user/${username}`);
    } catch (error) {
      alert(error);
    }
  };

  render() {
    const { t, i18n } = this.props;
    return (
      <div className="loginContainer">
        <form className="login-form">
          <div>
            <button onClick={async () => i18next.changeLanguage("it")}>
              it
            </button>
            <button onClick={async () => i18next.changeLanguage("en")}>
              en
            </button>
          </div>
          <h1>
            {this.props.t("loginpage.title", { framework: "react-i18next" })}
          </h1>
          <p>
            {" "}
            {this.props.t("loginpage.intro", { framework: "react-i18next" })}
          </p>
          <div id="inputFields">
            <input
              onChange={this.handleChange}
              name="username"
              type="text"
              ref={this.myUsernameInput}
              placeholder={this.props.t("loginpage.placeholderName", {
                framework: "react-i18next"
              })}
            />
            <input
              name="email"
              type="email"
              placeholder={this.props.t("loginpage.placeholderName", {
                framework: "react-i18next"
              })}
              ref={this.myEmailInput}
            />
            <input
              onChange={this.handleChange}
              name="password"
              type="password"
              placeholder="Password"
              ref={this.myPasswordInput}
            />
          </div>
          <p>
            {this.props.t("loginpage.questions", {
              framework: "react-i18next"
            })}
            <a href="mailto: ross.ferrandino@gmail.com">
              {this.props.t("loginpage.email", { framework: "react-i18next" })}
            </a>
            .
          </p>
          <button type="submit" onClick={this.handleSignUp}>
            Crea un nuovo user →{" "}
          </button>
          <button type="submit" onClick={this.handleLogin}>
            {this.props.t("loginpage.loginbutton", {
              framework: "react-i18next"
            })}
          </button>
        </form>
      </div>
    );
  }
}

export default translate("common")(Login);
