import React from "react";
import "./Location.css";

class Location extends React.Component {
  render() {
    return (
      <div className="location">
        <h2 className="title">Location</h2>
        <img className="location_image" src="/images/tenimento1.jpg" />
        <p className="description">
          In un'oasi fuori dal tempo, tra ulivi e lecci secolari, sorge
          Tenimento San Giuseppe, una tra le pi√π suggestive e antiche masserie
          in Puglia, un luogo ricco di charme, location da sogno per eventi di
          prestigio e per un indimenticabile matrimonio a Foggia e provincia. Il
          fascino di una dimora storica immersa nella splendida cornice naturale
          mediterranea.
        </p>
      </div>
    );
  }
}

export default Location;
